mapper.update:
update scrap
set SCOUNT = #{scount,jdbcType=DOUBLE},
  STIME = #{stime,jdbcType=TIMESTAMP},
  BID = #{bid,jdbcType=INTEGER},
  DEPARTID = #{departid,jdbcType=INTEGER}
where SID = #{sid,jdbcType=INTEGER}

mapper.select:
select SID, SCOUNT, STIME, BID, DEPARTID
from scrap
where SID = #{sid,jdbcType=INTEGER}
